import dotAsset     from '/card-assets/icons/spirit_dot-asset.svg';
import dotInfluence from '/card-assets/icons/spirit_dot-influence.svg';
import dotEmpty     from '/card-assets/icons/spirit_dot-empty.svg';
import dotDanger     from '/card-assets/icons/spirit_dot-danger.svg';

