// === Nunito embedded as data URI for SVGs only ===
// Paste your base64 payload into NUNITO_DATA. Use the correct MIME for your file.
const NUNITO_DATA = "data:font/woff2;base64,"; 
// If you're using TTF, change to: data:font/ttf;base64,...

// A <defs><style> block we can drop inside any inline <svg>
function SvgNunitoDefs() {
  return (
    <defs>
      <style>{`
@font-face {
  font-family: 'NunitoEmbed';
  src: url(${NUNITO_DATA}) format('${NUNITO_DATA.includes("font/ttf") ? "truetype" : "woff2"}');
  font-weight: 100 900;          /* variable range */
  font-style: normal;
  font-display: block;           /* no fallback flash inside SVG */
}
      `}</style>
    </defs>
  );
}



/* overlays */
