  Common:        'linear-gradient(180deg, #544788 0%, #544788 40%, #544788 100%)',
  Crafted:       'linear-gradient(180deg, #424c14 0%, #424c14 30%, #424c14 100%)',
  Enigmatic:     'linear-gradient(180deg, #1b4a4b 0%, #1b4a4b 100%)',
  Storied:       'linear-gradient(180deg, #4b184b 0%, #4b184b 100%)',
  Primordial:    'linear-gradient(180deg, #2c2925 0%, #2c2925 20%, #2c2925 80%, #2c2925 100%)',
  Exotic:        'linear-gradient(180deg, #560720 0%, #560720 100%)',
  Otherworldly:  'linear-gradient(175deg, #8C3B1B 0%, #8C3B1B 100%',
  'Core Challenge':'linear-gradient(135deg, #5f2716 0%, #5f2716 100%)',
