  Crafted:       'linear-gradient(135deg, #DFF7EE 0%, #BFE9D9 45%, #A5D7C6 100%)',
  Enigmatic:     'linear-gradient(135deg, #E6F6FF 0%, #BFDFF1 45%, #A9D1E8 100%)',
  Storied:       'linear-gradient(135deg, #F3E9FF 0%, #D1B7E6 45%, #BEA5D8 100%)',
  Primordial:    'linear-gradient(135deg, #FFF4D2 0%, #F5DB9E 45%, #E6CA86 100%)',
  Exotic:        'linear-gradient(135deg, #FFE8DD 0%, #F6C4AB 45%, #E8B399 100%)',
  Otherworldly:  'linear-gradient(135deg, #FFF1CC 0%, #E8D79F 45%, #DAC98E 100%)',
